mapper.select:
        SELECT

            DISTINCT
            c.NOTICE_ID,

            A.*,B.ENTERPRISE_NAME
        FROM (SELECT

        FROM

        ) A left join T_STORE_ENTERPRISE B on A.ENTERPRISE_ID = B.ID

            LEFT JOIN T_ROTATE_SCHEDULE c on A.id = c.NOTICE_ID



                AND A.NOTICE_SERIAL LIKE concat(concat('%',#{entity.noticeSerial}),'%')




	                AND A.ACCEPTE_UNIT IN

	                    #{unit}



                   AND B.ID IN

                       #{id}




            		AND B.SHORT_NAME LIKE concat(concat('%',#{entity.accepteUnit}),'%')



                AND A.STORAGE_DATE = #{entity.storageDate}


                AND A.DOCUMENT_NUMBER LIKE concat(concat('%',#{entity.documentNumber}),'%')


                AND A.CREATER LIKE concat(concat('%',#{entity.creater}),'%')


                AND to_char(A.CREATE_TIME,'yyyy-mm-dd') LIKE to_char(#{entity.createTime},'yyyy-mm-dd')


                AND A.STATUS = #{entity.status}


                AND to_char(A.COMPLETE_DATE,'yyyy-mm-dd') LIKE to_char(#{entity.completeDate},'yyyy-mm-dd')


                AND A.NOTICE_TYPE = #{entity.noticeType}


                AND A.SEND_STATUS = #{entity.sendStatus}


                AND A.ENTERPRISE_ID = #{entity.storeEnterprise.id}


                AND c.id is not null


                AND c.id is null


        ORDER BY to_number(substr(NOTICE_SERIAL,2,4)) desc,to_number(substr(NOTICE_SERIAL,7,(instr(NOTICE_SERIAL,'Âè∑')-7))) desc,CREATE_TIME DESC

        SELECT COUNT(ID) FROM



                AND NOTICE_SERIAL LIKE concat(concat('%',#{entity.noticeSerial}),'%')



	                AND ACCEPTE_UNIT IN

	                    #{unit}



            		AND ACCEPTE_UNIT LIKE concat(concat('%',#{entity.accepteUnit}),'%')



                AND STORAGE_DATE = #{entity.storageDate}


                AND DOCUMENT_NUMBER = #{entity.documentNumber}


                AND CREATER like '%${entity.creater}%'


                AND to_char(CREATE_TIME,'yyyy-mm-dd') LIKE to_char(#{entity.createTime},'yyyy-mm-dd')


                AND STATUS = #{entity.status}


                AND to_char(COMPLETE_DATE,'yyyy-mm-dd') LIKE to_char(#{entity.completeDate},'yyyy-mm-dd')


                AND NOTICE_TYPE = #{entity.noticeType}


                AND SEND_STATUS = #{entity.sendStatus}

mapper.update:
begin UPDATE
SET ROTATE_TYPE = #{rotateType,jdbcType=VARCHAR}, NOTICE_SERIAL = #{noticeSerial,jdbcType=VARCHAR}, DOCUMENT_NUMBER = #{documentNumber,jdbcType=VARCHAR}, ACCEPTE_UNIT = #{accepteUnit,jdbcType=VARCHAR}, STORAGE_DATE = #{storageDate,jdbcType=TIMESTAMP}, STATUS = #{status,jdbcType=VARCHAR}, COMPLETE_DATE = #{completeDate,jdbcType=TIMESTAMP}, COMPLETE_HUMEN = #{completeHumen,jdbcType=VARCHAR}, YEAR = #{year,jdbcType=VARCHAR}, REMARK = #{remark,jdbcType=VARCHAR}, ANNEX = #{annex,jdbcType=VARCHAR}, SEND_STATUS = #{sendStatus,jdbcType=NUMERIC}, "AUDIT" = #{audit,jdbcType=VARCHAR}, SENDER = #{sender,jdbcType=VARCHAR}, ENTERPRISE_ID=#{storeEnterprise.id,jdbcType=VARCHAR} WHERE ID = #{id};
DELETE FROM T_ROTATE_NOTICE_DETAIL WHERE NOTICE_ID = #{id};
INSERT INTO T_ROTATE_NOTICE_DETAIL () VALUES( #{notice.id,jdbcType=NVARCHAR}, #{id,jdbcType=NVARCHAR}, #{notice.serialNo,jdbcType=NUMERIC}, #{notice.variety,jdbcType=NVARCHAR}, #{notice.cost,jdbcType=NVARCHAR}, #{notice.pipeAttribute,jdbcType=NVARCHAR}, #{notice.harvestYear,jdbcType=NVARCHAR}, #{notice.storageWarehouse,jdbcType=NVARCHAR}, #{notice.storehouse,jdbcType=NVARCHAR}, #{notice.planNumber,jdbcType=NVARCHAR}, #{notice.actualNumber,jdbcType=NVARCHAR}, #{notice.removalUnit,jdbcType=NVARCHAR}, #{notice.immigrationUnit,jdbcType=NVARCHAR}, #{notice.dealSerial,jdbcType=NVARCHAR}, #{notice.warehouse.id,jdbcType=NVARCHAR}, #{notice.enterpriseId,jdbcType=VARCHAR}, #{notice.batchNumber,jdbcType=VARCHAR}, #{notice.manuReport,jdbcType=VARCHAR}, #{notice.manuActualNumber,jdbcType=NVARCHAR} );
end;

mapper.select:
SELECT, , w.WAREHOUSE_SHORT FROM
m LEFT JOIN T_ROTATE_NOTICE_DETAIL c ON m.ID = c.NOTICE_ID LEFT JOIN T_STORAGE_WAREHOUSE w ON c.WAREHOUSE_ID = w.id WHERE m.ID IN (
#{id}
)

SELECT rn FROM (
SELECT a.*,ROWNUM rn FROM T_ROTATE_NOTICE a
)

    AND ID = #{id}
    AND YEAR = #{year}

SELECT,

FROM

m
LEFT JOIN T_ROTATE_NOTICE_DETAIL c ON m.ID = c.NOTICE_ID


        AND c.DEAL_SERIAL = #{dealSerial}


        AND m.STATUS = #{status}


        AND m.ACCEPTE_UNIT = #{acceptUnit}

SELECT * FROM (
SELECT A.*, ROWNUM RN
FROM (SELECT *
FROM (SELECT,

FROM

m
LEFT JOIN T_ROTATE_NOTICE_DETAIL c ON m.ID = c.NOTICE_ID
ORDER BY M.CREATE_TIME DESC
) A


        AND NOTICE_SERIAL LIKE concat(concat('%',#{entity.noticeSerial}),'%')


        AND ACCEPTE_UNIT IN

            #{unit}



        AND STORAGE_DATE = #{entity.storageDate}


        AND DOCUMENT_NUMBER LIKE concat(concat('%',#{entity.documentNumber}),'%')


        AND CREATER LIKE concat(concat('%',#{entity.creater}),'%')


        AND to_char(CREATE_TIME,'yyyy-mm-dd') >= to_char(#{entity.createTime},'yyyy-mm-dd')


        AND STATUS = #{entity.status}


        AND to_char(COMPLETE_DATE,'yyyy-mm-dd') LIKE to_char(#{entity.completeDate},'yyyy-mm-dd')


        AND NOTICE_TYPE = #{entity.noticeType}


UNION
SELECT *
FROM (SELECT,

FROM

m
LEFT JOIN T_ROTATE_NOTICE_DETAIL c ON m.ID = c.NOTICE_ID
ORDER BY M.CREATE_TIME DESC
) A


        AND NOTICE_SERIAL LIKE concat(concat('%',#{entity.noticeSerial}),'%')


        AND ACCEPTE_UNIT IN

            #{unit}



        AND STORAGE_DATE = #{entity.storageDate}


        AND DOCUMENT_NUMBER LIKE concat(concat('%',#{entity.documentNumber}),'%')


        AND CREATER LIKE concat(concat('%',#{entity.creater}),'%')


        AND to_char(CREATE_TIME,'yyyy-mm-dd') <to_char(#{entity.createTime},'yyyy-mm-dd')


        AND STATUS = #{entity.status}


        AND to_char(COMPLETE_DATE,'yyyy-mm-dd') LIKE to_char(#{entity.completeDate},'yyyy-mm-dd')


        AND NOTICE_TYPE = #{entity.noticeType}

    AND SEND_STATUS = '0'

) A
WHERE ROWNUM <= #{pageIndex}*#{pageSize}
)
WHERE RN > (#{pageIndex} - 1) * #{pageSize}

select count(1) from 


        AND NOTICE_SERIAL = #{noticeSerial}


